import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [datetime, setDatetime] = useState("");
  const [description, setDescription] = useState("");
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    getTransactions().then(transactions =>{
      setTransactions(transactions)
    });
  }, []);

  async function getTransactions() {
    const url = process.env.REACT_APP_API_URL + "/transactions";
    const response = await fetch(url);
    return await response.json();
  }

  async function addTransaction(ev) {
    ev.preventDefault();
    if (!name || !price || !description || !datetime) {
      alert("Please Fill All the Details");
    } else {
      const url = process.env.REACT_APP_API_URL + "/transaction";
      fetch(url, {
        method: "POST",
        body: JSON.stringify({
          name: name.substring(0, 25),
          price,
          description,
          datetime,
        }),
        headers: { "Content-Type": "application/json" },
      }).then((response) => {
        response.json().then((json) => {
          setName("");
          setPrice("");
          setDatetime("");
          setDescription("");
          console.log("result", json);
        });
      });
    }
  };

  let balance = 0;
  for(const transaction of transactions){
    balance = balance + transaction.price;
  }
  return (
    <div>
      <nav className="navbar">
        <h1>MoneyTrack</h1>
        <button>Dark Mode</button>
        <main className="parent">
          <div className="formdiv">
            <h1>&#8377; {balance.toFixed(2)}</h1>
            <form onSubmit={addTransaction}>
              <div className="basics">
                <input
                  type="text"
                  value={name}
                  onChange={(ev) => setName(ev.target.value)}
                  placeholder={"New Entry Here"}
                />
                <input
                  type="text"
                  value={price}
                  onChange={(ev) => setPrice(ev.target.value)}
                  placeholder={"Credit/Debit: +400/-300 (in Rs)"}
                />
                <input
                  type="datetime-local"
                  value={datetime}
                  onChange={(ev) => setDatetime(ev.target.value)}
                />
              </div>
              <div className="description">
                <input
                  type="text"
                  value={description}
                  onChange={(ev) =>
                    setDescription(ev.target.value.substring(0, 100))
                  }
                  placeholder={"Description (Upto 100 characters)"}
                />
              </div>
              <button type="submit">Add Transaction</button>
            </form>
          </div>
          <div className="panel">
            {console.log(transactions)}
            {transactions.length > 0 &&
              transactions.map(transaction => (
                <div className="transaction">
                  <div className="left">
                    <div className="name">{transaction.name}</div>
                    <div className="itemdescription">{transaction.description}</div>
                  </div>
                  <div className="right">
                    <div className={"price" + (transaction.price < 0?"red":"green")}>&#8377; {transaction.price}</div>
                    <div className="datetime">{transaction.datetime}</div>
                  </div>
                </div>
              ))}
          </div>
        </main>
      </nav>
    </div>
  );
}

export default App;
